import React, {useState} from 'react'
import logo from './logo.svg'
import './App.css'

function App() {
  let [title, setTitle] = useState(['Í∞ïÎÇ® Ïö∞Îèô ÎßõÏßë', 'ÎÇ®Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú', 'Í∞úÎ∞úÌåÄ Î™®Ïßë'])
  let [date, setDate] = useState(['2Ïõî 18Ïùº Î∞úÌñâ', '3Ïõî 5Ïùº Î∞úÌñâ', '1Ïõî 13Ïùº Î∞úÌñâ'])
  let [thumbCount, setThumbCount] = useState(0)
  function titleChange() {
    let temp = [...title]
    temp.sort(function (a, b) {
      return b - a
    })
    setTitle(temp)
  }
  return (
    <div className="App">
      <div className="black-nav">
        <div>Í∞úÎ∞ú BLOG</div>
      </div>
      <button className="button" onClick={titleChange}>
        check
      </button>
      <div className="list">
        <h3>
          {' '}
          {title[0]}{' '}
          <span
            onClick={() => {
              setThumbCount(thumbCount + 1)
            }}
            style={{cursor: pointer}}
          >
            üëç
          </span>{' '}
          {thumbCount}
        </h3>
        <p>{date[0]}</p>
        <hr />
      </div>
      <div className="list">
        <h3> {title[1]} </h3>
        <p>{date[1]}</p>
        <hr />
      </div>
      <div className="list">
        <h3> {title[2]} </h3>
        <p>{date[2]}</p>
        <hr />
      </div>
    </div>
  )
}

export default App
