import React, {useState} from 'react'
import logo from './logo.svg'
import './App.css'

function App() {
  let [title, setTitle] = useState(['나이키', '아디다스', '퓨마']);
  let [date, setDate] = useState(['2월 2일', '3월 5일', '4월 6일'])
  let [modalNum, setModalNum] = useState();
  let [modal, setModal] = useState(false)
  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 BLOG</div>
      </div>
      { title.map((e, i) => {
        return (
          <div className="list">
            <h3 onClick={() => {
              setModalNum(i);
              modal === false ? setModal(true) : setModal(true);
            }}>
              {' '}
              { e }{' '}
            </h3>
            <p>{ date[i] }</p>
            <hr />
          </div>
        );  
      })}
      {modal === true ? <Modal title={title} date={date} modalNum={modalNum}></Modal> : null}
    </div>
  )
}

function Modal(props) {
  return (
    <div className="modal">
      <h2>{props.title[props.modalNum]}</h2>
      <p>{props.date[props.modalNum]}</p>
    </div>
  )
}

export default App
